import React from "react";
import Header from "../BooksSearchPage/Header.js";
import styles from "./Page.module.css";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
// let value;
function Page() {
  const [fet, onfet] = useState(false);
  const [resultk, onresultk] = useState(null);
  const { id } = useParams();
  const query = useSelector((state) => {
    return state.counter.query;
  });

  useEffect(() => {
    if (query !== " ") {
      fetch(
        `https://www.googleapis.com/books/v1/volumes?q=${id}&key=AIzaSyCDgRAXUYvnfJtU9bAOBlh0SU9Lu7MkhpE`
      )
        .then((e) => {
          return e.json();
        })
        .then((e) => {
          onfet(e);
        })
        .catch((error) => {
          console.log(error);
        });
    }
    // console.log(fet);
    // fet.items.forEach((e, i, arr) => {
    //   if (e.id === id) {
    //     onresultk(e);
    //   }
    // });
  }, []);
  // console.log(fet.items);
  useEffect(() => {
    fet.items?.forEach((e, i, arr) => {
      if (e.id === id) {
        onresultk(e);
      }
    });
  }, [fet.items]);

  return (
    <React.Fragment>
      <Header
        url={JSON.parse(localStorage.getItem("cred")).user.photoURL}
      ></Header>
      <section className={styles.body}>
        <div className={styles.img}>
          {/* <img src={resultk.volumeInfo?.imageLinks?.thumbnail} /> */}
        </div>
        <div className={styles.content}>
          <h1>Mahakali</h1>
          <p className={styles.authors}>
            <span className={styles.bold}>Authors :</span> Bijoy
          </p>
          <p className={styles.publishers}>
            <span className={styles.bold}>Publisher :</span> Oreily
          </p>
          <p className={styles.description}>
            <span className={styles.bold}>Description :</span> Introduces Java
            Data Objects and its capabilities, explains how to make classes
            persistent, how to configure JDO, how to make queries, how to
            perform transactions, and its use in Web applications and J2EE
            environments..
          </p>
          <p className={styles.pubdate}>
            <span className={styles.bold}>Publishing date :</span>
          </p>
          <p className={styles.page}>
            <span className={styles.bold}>Pages :</span>2389{" "}
          </p>
          <p className={styles.lang}>
            <span className={styles.bold}>Language :</span> English
          </p>
        </div>
      </section>
    </React.Fragment>
  );
}
export default Page;
