import "./App.css";
import Firstpage from "./Firstpage.js";
import Page from "./Page1.js";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import React from "react";
import { useSelector } from "react-redux";
import { Redirect } from "react-router-dom";
function App() {
  const log = useSelector((state) => state.counter.login);
  console.log(log);
  if (log !== " ") {
    console.log("here");
    localStorage.setItem("cred", log);
  }
  const el = localStorage.getItem("cred");
  return (
    <BrowserRouter>
      <div className="App">
        <Switch>
          <Route path="/" exact>
            {!log && <Firstpage></Firstpage>}
            {log && <Redirect to="/search"></Redirect>}
          </Route>

          <Route path="/search" exact>
            {log && <Page></Page>}
            {!log && el && <Page></Page>}
            {!log && !el && <Firstpage></Firstpage>}
          </Route>
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
