import styles from "./Books.module.css";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
function Books(props) {
  const [fet, onfet] = useState(false);
  const query = useSelector((state) => {
    return state.counter.query;
  });
  // console.log(query);
  useEffect(() => {
    fetch(
      `https://www.googleapis.com/books/v1/volumes?q=${query}&key=AIzaSyCDgRAXUYvnfJtU9bAOBlh0SU9Lu7MkhpE`
    )
      .then((e) => {
        return e.json();
      })
      .then((e) => {
        onfet(e.items);
        console.log(e);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [query]);

  return (
    <div className={styles.books}>
      {fet &&
        fet.map((e, i) => {
          return (
            <div className={styles.books1} key={i}>
              <img
                src={
                  e.volumeInfo.imageLinks.thumbnail
                    ? e.volumeInfo.imageLinks.thumbnail
                    : `https://www.bing.com/images/search?view=detailV2&ccid=RbEFvqqj&id=5980FC007B966F5056FF83E61DF3291A90D32EA5&thid=OIP.RbEFvqqjxfs6A0EovxGcTwHaHa&mediaurl=https%3a%2f%2fst4.depositphotos.com%2f14953852%2f22772%2fv%2f600%2fdepositphotos_227725020-stock-illustration-image-available-icon-flat-vector.jpg&exph=600&expw=600&q=No+Image+Available&simid=607988677350658451&FORM=IRPRST&ck=BE27685A092F162E994DBD2D70F805C5&selectedIndex=3&ajaxhist=0&ajaxserp=0`
                }
                alt={`${e.volumeInfo.title} book cover`}
              ></img>
              <article>
                <h1>{e?.volumeInfo?.title}</h1>
                <p>{e.volumeInfo?.authors?.join(" ")}</p>
              </article>
            </div>
          );
        })}
    </div>
  );
}
export default Books;
