import Header from "./Homepage/Header.js";
import "./App.css";
import SectionA from "./Homepage/SectionA.js";
import SectionB from "./Homepage/SectionB.js";
import SectionC from "./Homepage/SectionC.js";
import Footer from "./Homepage/Footer.js";
import Header1 from "./BooksSearchPage/Header.js";
import Books from "./BooksSearchPage/Books.js";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { Provider, useSelector } from "react-redux";
import store from "./store.js";
function App() {
  const [login, onlogin] = useState(undefined);
  const logid = useSelector((state) => state.counter.login);

  useEffect(() => {
    if (logid) {
      onlogin(JSON.parse(logid));
    }
  }, []);

  return (
    <BrowserRouter>
      <Provider store={store}>
        <div className="App">
          <Switch>
            <Route path="/" exact>
              <React.Fragment>
                <Header></Header>
                <SectionA></SectionA>
                <SectionB></SectionB>
                <SectionC></SectionC>
                <Footer></Footer>
              </React.Fragment>
            </Route>

            <Route path="/search" exact>
              {!login && window.location.assign("/")}
              <Header1 url={login?.user?.photoURL}></Header1>
              <Books></Books>
            </Route>
          </Switch>
        </div>
      </Provider>
    </BrowserRouter>
  );
}

export default App;
