import Header from "./Homepage/Header.js";
import "./App.css";
import SectionA from "./Homepage/SectionA.js";
import SectionB from "./Homepage/SectionB.js";
import SectionC from "./Homepage/SectionC.js";
import Footer from "./Homepage/Footer.js";
import Header1 from "./BooksSearchPage/Header.js";
import Books from "./BooksSearchPage/Books.js";
// import { signInWithGoogle } from "./firebase-config.js";
import { BrowserRouter, Redirect, Route, Switch } from "react-router-dom";
import React, { useEffect, useState } from "react";

function App() {
  const [login, onlogin] = useState();
  const [searchdata, onsearchdata] = useState();
  useEffect(() => {
    onlogin(JSON.parse(localStorage.getItem("credentials")));
  }, []);
  // console.log(login);
  // localStorage.clear();
  function getdata(e) {
    onsearchdata(e);
  }
  return (
    <BrowserRouter>
      <div className="App">
        <Switch>
          <Route path="/" exact>
            <React.Fragment>
              <Header></Header>
              <SectionA></SectionA>
              <SectionB></SectionB>
              <SectionC></SectionC>
              <Footer></Footer>
            </React.Fragment>
          </Route>

          <Route path="/search" exact>
            <Header1 url={login?.user?.photoURL} data={searchdata}></Header1>
            <Books data={getdata}></Books>
          </Route>
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
