import styles from "./Books.module.css";
import { useEffect, useState } from "react";
function Books(props) {
  const [fet, onfet] = useState(false);
  const [query, onquery] = useState();
  onquery(props.query);

  useEffect(() => {
    fetch(
      `https://www.googleapis.com/books/v1/volumes?q=${
        query === " " ? "A" : query
      }&key=AIzaSyCDgRAXUYvnfJtU9bAOBlh0SU9Lu7MkhpE`
    )
      .then((e) => {
        return e.json();
      })
      .then((e) => {
        // console.log(e.items);
        onfet(e.items);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className={styles.books}>
      {fet &&
        fet.map((e, i) => {
          return (
            <div className={styles.books1} key={i}>
              <img
                src={e.volumeInfo.imageLinks.thumbnail}
                alt={`${e.volumeInfo.title} book cover`}
              ></img>
              <article>
                <h1>{e.volumeInfo.title}</h1>
                <p>{e.volumeInfo?.authors?.join(" ")}</p>
              </article>
            </div>
          );
        })}
    </div>
  );
}
export default Books;
