import Header from "./Homepage/Header.js";
import "./App.css";
import SectionA from "./Homepage/SectionA.js";
import SectionB from "./Homepage/SectionB.js";
import SectionC from "./Homepage/SectionC.js";
import Footer from "./Homepage/Footer.js";
import Header1 from "./BooksSearchPage/Header.js";
import Books from "./BooksSearchPage/Books.js";
import { BrowserRouter, Redirect, Route, Switch } from "react-router-dom";
import React, { useState } from "react";

function App() {
  const [login, onlogin] = useState();
  function auth(m) {
    onlogin(m);
  }
  // console.log(login);
  return (
    <BrowserRouter>
      <div className="App">
        <Switch>
          <Route path="/" exact>
            {login && (
              <React.Fragment>
                <Header onAuth={auth}></Header>
                <SectionA></SectionA>
                <SectionB></SectionB>
                <SectionC></SectionC>
                <Footer></Footer>
              </React.Fragment>
            )}
          </Route>

          <Route path="/search" exact>
            {!login && (
              <React.Fragment>
                <Header1></Header1>
                <Books></Books>
              </React.Fragment>
            )}
          </Route>
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
