import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  query: "A",
};
export const slice = createSlice({
  name: "query",
  initialState,
  reducers: {
    update: (state, action) => {
      state.query = action.payload;
    },
  },
});
export const authActions = slice.actions;
export default slice.reducer;
